int* start_addr = note_array;
int* end_addr = start_addr + 807;

int* key_start = key_poitions;
int* key_end = key_start + 16;

//mutex to ensure we do not try to play keys while moving 
SemaphoreHandle_t ctrl_Mutex = xSemaphoreCreateMutex();

void setup() {
  Serial.begin(115200);

  // handle ctrlr mutex allocation failure
  if (ctrl_Mutex == nullptr) {
    Serial.println("Failed to create mutex!");
    while (1);
  }

  //set correct notes to play to global variable
  notes = &note_array[0]; //let notes point to first element of note array
  key_positions = &key_poitions[0];
  // key_positions = keys_t; //let key_positions point to correct key position array
  
  //intialise stepper and move to first position
  init_t_ctrl();

  //initialise voice coil controller for key playing with kp ki kd values 
  init_controller(0.5f, 0.1f, 0.005f);

}  

void loop() {
  // load next song here

  //wraparound song
  
  if(notes >= end_addr){
    Serial.println("Resetting song");
    notes = start_addr; //reset notes to start of array
  }
  
  if(key_positions >= key_end){
    Serial.println("Resetting key positions");
    key_positions = key_start; //reset key positions to start of array
  }
  
}

