int* start_addr = note_array;
int* end_addr = start_addr + 714;

int* key_start = keys;
int* key_end = key_start + 17;

//mutex to ensure we do not try to play keys while moving 

void setup() {
  Serial.begin(115200);

  next_note_ptr = &note_array[0]; //let next_note_ptr point to first element of note array
  next_key_ptr = &keys[0]; // let next_key_ptr point to first element of key position array 
  
  //init hardware drivers 
  init_t_ctrl(); // stepper 
  init_controller(0.5f, 0.1f, 0.005f); // voice coil

  // --- Wire tasks together ---
  coordinator_init(
      vcTaskHandle,
      t_controllerTaskHandle
  );

}  

void loop() {
  // load next song here

  //wraparound song
  
  if(next_note_ptr >= end_addr){
    Serial.println("Resetting song");
    next_note_ptr = start_addr; //reset notes to start of array
  }
  
  if(next_key_ptr >= key_end){
    Serial.println("Resetting key positions");
    next_key_ptr = key_start; //reset key positions to start of array
  }
  
}

