// ---  target sweep ---
// float target_voltage = 1.5f; // initial target in volts
//key positions (voltages for now)
const float PRESSED = 2.4f;
const float RELEASED = 1.1f;


void setup() {
  Serial.begin(115200);
  float p = 2.0f;
  float i = 0.0f;
  float d = 0.0f;
  //vertical motion controller (note playing for 1 finger)
  //initialize linear motion sensor and actuator
  init_controller(p, i, d);

}

void loop() {
  //release for 1.5 seconds
  float timer_start = millis() / 1000.0f;
  while (millis() / 1000.0f - timer_start < 1.5f) {
    set_target(RELEASED);
  }

  //hold for 4 seconds
  timer_start = millis() / 1000.0f;
  while (millis() / 1000.0f - timer_start < 4.0f) {
    set_target(PRESSED);
  }

}

